/*********************************************************************
*
*       CreateMessage
*/
void FUN_ICON9Clicked(void)
{
  WM_HWIN hWin;
  OS_ERR  err;
  uint8_t newmessadd=0,IsRead=0;
  uint8_t timecount=0;
  char messagename[20];
  OS_INFO("MessageDLG create\n");  
  com_createdir(MESSAGE_PATHDIR);
  hWin = GUI_CreateDialogBox(_aDialogCreateMessage, GUI_COUNTOF(_aDialogCreateMessage), _cbDialogMessage, HDTWIN, 0, 0);
  GUI_Delay(100);
  /* 初始化并检测模块 */
	if(sim900a_init()!= SIM900A_TRUE)
	{
    sim900a_flag=0;
    ErrorDialog(hWin,"GSM Error","SIM900A Module cannot work!");
    while(1)
    {
      if(tpad_flag)WM_DeleteWindow(hWin);
       if(!Flag_ICON9)return;
       GUI_Delay(10);
    }
	}
  sim900a_flag=1;
  LISTVIEW_AddItem(hWin);   
  sim900a_tx_printf("AT+CNMI=2,1\r");
  SIM900A_DELAY(100);  
  sim900a_tx_printf("AT+CMGF=1\r");           //文本模式
  SIM900A_DELAY(100);
  SIM900A_CLEAN_RX();//清除接收缓存
  while(Flag_ICON9)
	{
    if(timecount>100)
		{
			newmessadd=IsReceiveMS();      
			if(newmessadd)
			{	
				SIM900A_DELAY(500); 
        sim900a_tx_printf("AT+CSCS=\"UCS2\"\r");     //"GSM"字符集        
        SIM900A_DELAY(500);        
				BEEP_ON;
        OSSchedLock(&err);
        SIM900A_CLEAN_RX();//清除接收缓存
        com_data2null(&comdata[1000],2000);
        SIM900A_DELAY(100);
				IsRead=readmessage(newmessadd,(char *)&comdata[1000],(char *)&comdata[1200]); 
				printf("IsRead->(%d)\n",IsRead);
        if(IsRead)
				{
					hexuni2gbk((char *)&comdata[1000],messagename);	
					hexuni2gbk((char *)&comdata[1200],(char *)&comdata[2000]);	
          printf("name->(%s) mes->(%s)\n",messagename,(char *)&comdata[2000]);          
					saveReceiveMessage(hWin,messagename,(char *)&comdata[2000]);					
				}     
        OSSchedUnlock(&err);
        sim900a_tx_printf("AT+CMGD=%d\r",newmessadd);
        SIM900A_DELAY(100);
			}      
			SIM900A_CLEAN_RX();
			BEEP_OFF;
			timecount=0;      
		}
		timecount++;
    if(tpad_flag)WM_DeleteWindow(hWin);
		GUI_Delay(10);
  }
}

